CREATE TABLE shop (
    `shop_prefecturesCode` INT,
    `shop_lineCode` INT,
    `shop_stationCode` INT,
    `shop_key` INT,
    `shop_levels` INT,
    `shop_tags` VARCHAR(14) CHARACTER SET utf8,
    `shop_value` VARCHAR(25) CHARACTER SET utf8,
    `shop_streetAddress` VARCHAR(29) CHARACTER SET utf8,
    `shop_phoneNumber` VARCHAR(24) CHARACTER SET utf8,
    `shop_businessHours` VARCHAR(191) CHARACTER SET utf8,
    `shop_regularHoliday` VARCHAR(81) CHARACTER SET utf8,
    `shop_smoking` VARCHAR(40) CHARACTER SET utf8,
    `shop_parkingLot` VARCHAR(87) CHARACTER SET utf8,
    `shop_externalLink` VARCHAR(130) CHARACTER SET utf8,
    `shop_remarks` VARCHAR(736) CHARACTER SET utf8,
    `shop_updateDate` VARCHAR(30) CHARACTER SET utf8
);
